local screenGui = Instance.new("ScreenGui")\nlocal frame = Instance.new("Frame")\nlocal textLabel = Instance.new("TextLabel")\nlocal playButton = Instance.new("TextButton")\n\n-- Set the properties of the ScreenGui\nscreenGui.Name = "LoadingScreen"\nscreenGui.Parent = game.Players.LocalPlayer:WaitForChild("PlayerGui")\n\n-- Set the properties of the Frame\nframe.Parent = screenGui\nframe.BackgroundColor3 = Color3.fromRGB(0, 0, 0)\nframe.Size = UDim2.new(1, 0, 1, 0)\n\n-- Set the properties of the TextLabel\ntextLabel.Parent = frame\ntextLabel.Size = UDim2.new(1, 0, 0.5, 0)\ntextLabel.Position = UDim2.new(0, 0, 0.25, 0)\ntextLabel.Text = "Loading..."\ntextLabel.TextColor3 = Color3.fromRGB(255, 255, 255)\ntextLabel.TextScaled = true\n\n-- Set the properties of the Play Button\nplayButton.Parent = frame\nplayButton.Size = UDim2.new(0.3, 0, 0.1, 0)\nplayButton.Position = UDim2.new(0.5, -75, 0.75, -25)\nplayButton.BackgroundColor3 = Color3.fromRGB(0, 170, 0)\nplayButton.Text = "Play"\nplayButton.TextColor3 = Color3.fromRGB(255, 255, 255)\nplayButton.TextScaled = true\n\nplayButton.MouseButton1Click:Connect(function()\n    screenGui:Destroy() -- Remove loading screen on play button click\nend\n
